RSpec.describe DiaryEntry do
  it do
    diary = DiaryEntry.new('First Entry', 'I started a diary!')
    expect(diary.title).to eq 'First Entry'
    expect(diary.contents).to eq 'I started a diary!'
    expect(diary.count_words).to eq 4
    expect(diary.reading_time(2)).to eq 2
    expect(diary.reading_time(200)).to eq 1
    expect(diary.reading_chunk(3, 1)).to eq 'I started a'
    expect(diary.reading_chunk(3, 1)).to eq 'diary!'
    expect(diary.reading_chunk(3, 1)).to eq 'I started a'
  end

  it do
    diary = DiaryEntry.new('My second diary entry', lorem)
    expect(diary.title).to eq 'My second diary entry'
    expect(diary.contents).to eq lorem
    expect(diary.count_words).to eq 7000
    expect(diary.reading_time(160)).to eq 44
    expect(diary.reading_time(200)).to eq 35
    expect(diary.reading_chunk(200, 15)).to eq lorem.split(" ")[0..2999].join(" ")
    expect(diary.reading_chunk(200, 10)).to eq lorem.split(" ")[3000..4999].join(" ")
    expect(diary.reading_chunk(200, 15)).to eq lorem.split(" ")[5000..-1].join(" ")
    expect(diary.reading_chunk(200, 15)).to eq lorem.split(" ")[0..2999].join(" ")
  end
end
